import React, { useState, useEffect } from "react";
import { Col, Row, Card, Spinner } from "antd";
import { apiInstance } from "../utils/api";

const Bookings = () => {
  const [bookings, setBookings] = useState([]);
  const [loading, setLoading] = useState(false);

  //closure excecution
  useEffect(() => {
    (async () => {
      try {
        setLoading(true);
        const response = await apiInstance.get("/bookings");
        const data = response.data; // getting data
        if (data.success) {
          setBookings(data.data);
        }
      } catch (error) {
        console.log(error);
      }
      setLoading(false);
    })();
  }, []);

  return (
    <div
      style={{
        display: "flex",
        justifyContent: "center",
        paddingTop: "4rem",
        backgroundColor: "#eee",
      }}
    >
      <div
        style={{
          width: "100%",
          maxWidth: "850px",
          display: "flex",
          flexDirection: "column",
        }}
      >
        <h3>My Bookings</h3>
        <Row>
            {
                loading?(<Spinner/>):
                <Col span={6}>
                <Card
                  title="Default size card"
                  extra={<a href="#">More</a>}
                  style={{
                    width: 300,
                  }}
                >
                  <p>Card content</p>
                  <p>Card content</p>
                  <p>Card content</p>
                </Card>
                </Col>
            }
          <Col span={6}>col</Col>
          <Col span={6}>col</Col>
          <Col span={6}>col</Col>
        </Row>
      </div>
    </div>
  );
};

export default Bookings;
